import './App.css';
import React from 'react';


class NavContent extends React.Component {
  state = {
    position: "relative",
    margin: "20px",
  }

  render() {
    return (
      <div style={this.state}>
        {this.props.content}
      </div>
    );
  }
}

class NavHeader extends React.Component {
  state = {
    display: "inline-flex",
    position: "sticky",
    width: "100%",
    height: "fit-content",
    color: "white",
    "background-color": "black",
    "box-shadow": "0px 10px 20px black",
    name: "Vibe Chat"
  };

  render() {

    this.setState(this.props.style);

    return (
      <nav style={this.state}>
        {this.props.children}
        {/* <input type='button' onClick={() => {
          const req = axios.get("http://localhost:3020/kesa");
          req.then(resp => {
            this.setState({
              name: resp.data.content
            });

            if (this.state.name != "Vibe Chat") {
              this.setState({
                name: "Vibe Chat"
              });
            }
          });
          this.setState({
            "background-color": "blue"
          })
        }} value="Press" /> */}
      </nav>
    );
  }
}

class InputComponent extends React.Component {
  state = {
    styles: {
      margin: "15px",
      padding: "10px",
      width: this.props.width,
      "border-radius": "20px"
    }
  }

  render() {
    return (
      <input type={this.props.type} value={this.props.value} style={this.state.styles} />
    );
  }
}

