class MsgComponent extends React.Component {
  state = {
    msg: {
      position: "relative",
      width: "max-content",
      height: "max-content",
      float: this.props.float,
      padding: "8px",
      margin: "15px",
      "margin-bottom": "30px",
      color: "white",
      "background-color": "purple"
    }
  }

  render() {
    return (
      <div style={this.state}>
        <div style={this.state.msg}>
        {this.props.value}
        </div>
      </div>
    );
  }
}

class BodyArea extends React.Component {
  state = {
    styles: {
      display: "flex",
      "flex-direction": "column-reverse",
      position: "absolute",
      width: "100%",
      height: "90%",
    },
    input_div: {
      display: "inline-flex",
      border: "2px solid grey",
      "border-radius": "23px",
      "background-color": "black"
    },
    msg_box: {
      display: "flex",
      "flex-direction": "column-reverse",
      position: "relative",
      height: "100%",
      width: "100%"
    }
  };

  render() {
    return (
      <div style={this.state.styles}>

        <div style={this.state.input_div}>
          <InputComponent type="button" value="Start" width="15%" />
          <InputComponent type="text" value="Enter Value" width="60%" />
          <InputComponent type="button" value="Send" width="15%" />
        </div>

        <div style={this.state.msg_box}>
          <MsgComponent float="right" value="Hi Viraj" />
          <MsgComponent float="right" value="Hi Viraj" />
          <MsgComponent float="left" value="Hi Viraj" />
        </div>
      </div>
    );
  }
}

function App() {
  return (
    <div>
      <NavHeader>
        <NavContent content="Vibe Chat" />
      </NavHeader>
      <BodyArea />
    </div>
  );
}

export default App;
