import './App.css';
import React from 'react';

class NavContent extends React.Component {
  state = {
    position: "relative",
    margin: "100px"
  }

  render() {
    return (
      <div>
        {this.props.content}
      </div>
    );
  }
}

class NavHeader extends React.Component {
  state = {
    display: "inline-flex",
    width: "100%",
    height: "fit-content",
    color: "white",
    "background-color": "black"
  };

  render() {
    return (
      <nav style={this.state}>
        <NavContent content="Vibe Chat"/>
        <NavContent content=":" />
        <input type='button' onClick={() => {
          this.setState({
            "background-color": "blue"
          })
        }} value="Press" />
      </nav>
    );
  }
}


function App() {
  return (
    <NavHeader />
  );
}

export default App;
