import './App.css';
import React from 'react';


class NavContent extends React.Component {
  state = {
    position: "relative",
    margin: "20px",
  }

  render() {
    return (
      <div style={this.state}>
        {this.props.content}
      </div>
    );
  }
}

class NavHeader extends React.Component {
  state = {
    display: "inline-flex",
    position: "relative",
    top: "0",
    margin: 0,
    width: "100%",
    color: "white",
    "background-color": "black",
    "box-shadow": "0px 6px 20px grey"
  };

  render() {

    // this.setState(this.props.style);

    return (
      <nav style={this.state}>
        {this.props.children}
        {/* <input type='button' onClick={() => {
          const req = axios.get("http://localhost:3020/kesa");
          req.then(resp => {
            this.setState({
              name: resp.data.content
            });

            if (this.state.name != "Vibe Chat") {
              this.setState({
                name: "Vibe Chat"
              });
            }
          });
          this.setState({
            "background-color": "blue"
          })
        }} value="Press" /> */}
      </nav>
    );
  }
}

class InputComponent extends React.Component {
  state = {
    styles: {
      margin: "15px",
      padding: "10px",
      width: this.props.width,
      "border-radius": "20px",
    }
  }

  render() {
    return (
      <input type={this.props.type} style={this.state.styles} value={this.props.value} className = "inputs" />
    );
  }
}

class MsgComponent extends React.Component {
  state = {

    position: "relative",
    width: "max-content",
    height: "max-content",
    float: this.props.float,
    padding: "8px",
    margin: "15px",
    "margin-bottom": "30px",
    color: "white",
    "background-color": "purple",
    "border-radius": "15px 15px 15px 0"
  }

  render() {

    if ("left".localeCompare(this.props.float.toString())) {
      this.setState({
        "background-color": "red", 
        "border-radius": "15px 15px 0px 15px"
      });
    }
    return (
      <div>
        <div style={this.state}>
          {this.props.value}
        </div>
      </div>
    );
  }
}

class BodyArea extends React.Component {
  state = {
    styles: {
      display: "inline-flex",
      "flex-direction": "column-reverse",
      position: "fixed",
      width: "100%",
      height: "calc(10%)",
      "min-height": "100%",
    },
    input_div: {
      display: "inline-flex",
      position: "sticky",
      border: "2px solid grey",
      "border-radius": "23px",
      "background-color": "black"
    },
    msg_box: {
      display: "flex",
      "flex-direction": "column-reverse",
      position: "relative",
      height: "calc(100%)",
      width: "100%",
      overflow: "scroll",
      // "border": "1px solid white",
      "background-color": "yellow",
      "background-image": "linear-gradient(to bottom right, white, grey, rgb(73, 73, 73), rgb(27, 27, 27))",
      "background-size": "0%",
      "background-repeat": "no-repeat",
      "background-attachment": "fixed"
    }
  };

  render() {
    return (
      <div style={this.state.styles}>

        <div style={this.state.input_div}>
          <InputComponent type="button" value="Start" width="15%" />
          <InputComponent type="text" value="Enter Value" width="60%" />
          <InputComponent type="button" value="Send" width="15%" />
        </div>

        <div className="msg_box" style={this.state.msg_box}>
          <MsgComponent float="right" value="Hi Khushi" />
          <MsgComponent float="left" value="Hi Viraj" />
        </div>
        <NavHeader>
          <NavContent content="Vibe Chat" />
        </NavHeader>
      </div>
    );
  }
}

function App() {
  return (
    <BodyArea />
  );
}

export default App;
