import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

class NavContent extends React.Component {
  state = {
    position: "relative",
    margin: "20px",
  }

  render() {
    return (
      <div style={this.state}>
        {this.props.content}
      </div>
    );
  }
}

class NavHeader extends React.Component {
  state = {
    display: "inline-flex",
    position: "sticky",
    width: "100%",
    height: "fit-content",
    color: "white",
    "background-color": "black",
    "box-shadow": "8px 10px 12px grey",
    name: "Vibe Chat"
  };

  render() {
    return (
      <nav style={this.state}>
        <NavContent content={this.state.name} />
        <NavContent content=":" />
        {/* <input type='button' onClick={() => {
          const req = axios.get("http://localhost:3020/kesa");
          req.then(resp => {
            this.setState({
              name: resp.data.content
            });

            if (this.state.name != "Vibe Chat") {
              this.setState({
                name: "Vibe Chat"
              });
            }
          });
          this.setState({
            "background-color": "blue"
          })
        }} value="Press" /> */}
      </nav>
    );
  }
}

class InputComponent extends React.Component {
  state = {
    styles: {
      div: {
        height: "10%",
        width: "100%"
      }
    },
    properties : {
      value: "Start"
    }
  }

  render(){
    return (
      <div style={this.state.styles.div}>
        <input type='button' value={this.state.properties.value} />
      </div>
    );
  }
}

class ChatArea extends React.Component {
  state = {
    display: "flex",
    "flex-direction": "column-reverse",
    position: "absolute",
    float: "center",
    "justify-self": "center",
    width: "90%",
    height: "90%",
    "border-radius": "20px",
    "background-image": "linear-gradient(rgb(62, 19, 19), rgb(32, 19, 29))"
  };

  render() {
    return (
      <div style={this.state}>
        <InputComponent/>
      </div>
    );
  }
}

class BodyArea extends React.Component {
  state = {
    styles: {
      display: "flex",
      position: "absolute",
      width: "100%",
      height: "90%",
      "justify-content": "center",
      "align-items": "center",
      border: "2px solid black"
    }
  };

  render() {
    return (
      <div style={this.state.styles}>
        <ChatArea />
      </div>
    );
  }
}

function App() {
  return (
    <div>
      <NavHeader />
      <BodyArea />
    </div>
  );
}

export default App;
