import './App.css';
import React, { useEffect, useState } from 'react';

class NavContent extends React.Component {
  state = {
    position: "relative",
    margin: "20px",
  }

  render() {
    return (
      <div style={this.state}>
        {this.props.content}
      </div>
    );
  }
}

class NavHeader extends React.Component {
  state = {
    display: "inline-flex",
    width: "100%",
    height: "fit-content",
    color: "white",
    "background-color": "black",
    "box-shadow": "8px 10px 12px green"
  };

  render() {
    return (
      <nav style={this.state}>
        <NavContent content="Vibe Chat" />
        <NavContent content=":" />
        <input type='button' onClick={() => {
          this.setState({
            "background-color": "blue"
          })
        }} value="Press" />
      </nav>
    );
  }
}

class BodyArea extends React.Component {
  state = {
    styles: {
      display: "flex",
      position: "absolute",
      width: "100%",
      height: "100%",
      "justify-content": "center",
      "align-items": "center",
      border: "2px solid black"
    }
  };

  render() {
    return (
      <div style={this.state.styles}>
        <ChatArea />
      </div>
    );
  }
}

class ChatArea extends React.Component {
  state = {
    display: "flex",
    position: "absolute",
    float: "center",
    "justify-self": "center",
    width: "60%",
    height: "80%",
    "background-color": "yellow",
    "box-shadow": "22px 22px 20px grey"
  };

  render() {
    return (
      <div style={this.state}>
      </div>
    );
  }
}

function App() {
  return (
    <div>
      <NavHeader />
      <BodyArea />
    </div>
  );
}

export default App;
