import './App.css';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

class NavContent extends React.Component {
  state = {
    position: "relative",
    margin: "20px",
  }

  render() {
    return (
      <div style={this.state}>
        {this.props.content}
      </div>
    );
  }
}

class NavHeader extends React.Component {
  state = {
    display: "inline-flex",
    position: "sticky",
    width: "100%",
    height: "fit-content",
    color: "white",
    "background-color": "black",
    "box-shadow": "8px 10px 12px grey",
    name: "Vibe Chat"
  };

  render() {
    if(this.props.style.length > 0){
    this.setState(this.props.style);
    }
    return (
      <nav style={this.state}>
        {this.props.children}
        {/* <input type='button' onClick={() => {
          const req = axios.get("http://localhost:3020/kesa");
          req.then(resp => {
            this.setState({
              name: resp.data.content
            });

            if (this.state.name != "Vibe Chat") {
              this.setState({
                name: "Vibe Chat"
              });
            }
          });
          this.setState({
            "background-color": "blue"
          })
        }} value="Press" /> */}
      </nav>
    );
  }
}

class InputComponent extends React.Component {
  state = {
    styles: {
      margin: "15px",
      padding: "10px",
      width: this.props.width,
      "border-radius": "20px" 
    }
  }

  render() {
    return (
      <input type={this.props.type} value={this.props.value} style={this.state.styles} />
    );
  }
}

class ChatArea extends React.Component {
  state = {
    outer_div: {
      display: "flex",
      "flex-direction": "column-reverse",
      position: "absolute",
      float: "center",
      "justify-self": "center",
      width: "90%",
      height: "90%",
      "border-radius": "20px",
      "background-image": "linear-gradient(rgb(62, 19, 19), rgb(32, 19, 29))"
    },

    inner_div: {
      display: "inline-flex",
      border: "2px solid red",
      "border-radius": "23px"
    },

    Nav: {
      display: "inline-flex",
      position: "absolute",
      color: "yellow",
      "background-color": "yellow"
    }
  };

  render() {
    return (
      <div style={this.state.outer_div}>

        <div style={this.state.inner_div}>
          <InputComponent type="button" value="Start" width="15%" />
          <InputComponent type="text" value="Enter Value" width="60%" />
          <InputComponent type="button" value="Send" width="15%" />
        </div>

        <NavHeader style={this.state.Nav}>
          <NavContent content="StrangerConnected" />
        </NavHeader>
      </div>
    );
  }
}

class BodyArea extends React.Component {
  state = {
    styles: {
      display: "flex",
      position: "absolute",
      width: "100%",
      height: "90%",
      "justify-content": "center",
      "align-items": "center",
      border: "2px solid black"
    }
  };

  render() {
    return (
      <div style={this.state.styles}>
        <ChatArea />
      </div>
    );
  }
}

function App() {
  return (
    <div>
      <NavHeader>
        <NavContent content="Vibe Chat" />
      </NavHeader>
      <BodyArea />
    </div>
  );
}

export default App;
