// Dependencies
var restful = require('node-restful');
var mongoose = restful.mongoose;
var Mixed = mongoose.Schema.Types.Mixed;

// Schema
var eventoSchema = new mongoose.Schema({
	evento: String,
	horarioAgendaDe: String,
	horarioAgendaAte: String,
	categoria: Mixed,
	artista: String,
	sinopse: String,
	texto: String,
	direcao: String,
	elenco: String,
	genero: Mixed,
	classificacao: Mixed,
	duracao: String,
	regencia: String,
	horario: String,
	dataAbertura: Mixed,
	datas: [Mixed],
	local: Mixed,
	ingresso: String,
	trailer: String,
	destaque: Boolean,
	realizacao: String,
	promocao: String,
	curadoria: String,
	curador: String,
	imagem: String
});

// Return model
module.exports = restful.model('Eventos', eventoSchema);