// Dependencies
var restful = require('node-restful');
var fs = require('fs');
var mongoose = restful.mongoose;

// Schema
var informacaoSchema = new mongoose.Schema({
  titulo: String,
  descricao: String,
  texto: String,
  imagem: String
});

// Return model
module.exports = restful.model('Informacoes', informacaoSchema);
