// Dependencies
var express = require('express');
var router = express.Router();

// Models
var Evento = require('../models/evento');
var Genero = require('../models/genero');
var Informacao = require('../models/informacao');
var Classificacao = require('../models/classificacao');
var Local = require('../models/local');

// Routes
Evento.methods(['get', 'put', 'post', 'delete']);
Evento.register(router, '/eventos');

Genero.methods(['get', 'put', 'post', 'delete']);
Genero.register(router, '/generos');

Informacao.methods(['get', 'put', 'post', 'delete']);
Informacao.register(router, '/informacoes');

Classificacao.methods(['get', 'put', 'post', 'delete']);
Classificacao.register(router, '/classificacoes'); 

Local.methods(['get', 'put', 'post', 'delete']);
Local.register(router, '/locais');

