// Dependencies
var restful = require('node-restful');
var mongoose = restful.mongoose;
var Mixed = mongoose.Schema.Types.Mixed;

// Schema
var localSchema = new mongoose.Schema({
	local: String,
	endereco: String,
	horarioFuncionamento: String,
	telefones: String,
	email: String,
	site: String,
	youtube: String,
	twitter: String,
	facebook: String,
	categorias: [Mixed], //vetor de objetos
	imagem: String,
	linkMapa: String,
});

// Return model
module.exports = restful.model('Locais', localSchema);